select c.user_id as user_id1, d.user_id as user_id2, count(c.business_id) as similarity from (select a.user_id as user_id, b.business_id as business_id from (select user_id from review group by user_id having count(user_id) > 200) as a left join (select user_id, business_id from review where business_id is not null union select user_id, business_id from tip where business_id is not null) as b on a.user_id = b.user_id) as c, (select a.user_id as user_id, b.business_id as business_id from (select user_id from review group by user_id having count(user_id) > 200) as a left join (select user_id, business_id from review where business_id is not null union select user_id, business_id from tip where business_id is not null) as b on a.user_id = b.user_id) as d where c.user_id != d.user_id and c.business_id = d.business_id group by user_id1, user_id2 order by count(c.business_id) desc limit 1;